import React from 'react';
import PostsService from '../service/PostsService';
import { Link } from 'react-router-dom';
import { Typography, Container } from "@material-ui/core/";
import { makeStyles } from '@material-ui/core/styles'
import KeyboardBackspaceIcon from '@material-ui/icons/KeyboardBackspace';
import Button from '@material-ui/core/Button';
import Disqus from "disqus-react";

const useStyles = makeStyles(() => ({
  link: {
      textDecoration: 'none'
  }
}))
export default function Read({ match }) {
  const classes = useStyles()

  const slug = match.params.slug.replace('-', ' ')
  const post = PostsService.find(post => post.title == slug)

  const disqusShortname = "bahyusanciko-1";
  const disqusConfig = {
    url: match.url,
    identifier: 'PAGE_IDENTIFIER',
    title: post.title
  };

  return (
  <Container component="main" maxWidth="sm">
  <Typography variant="h3" component="h1" gutterBottom>  <Link to="/blog" ><KeyboardBackspaceIcon/></Link>
{post.title}</Typography>
            <Typography variant="h5" component="h2" gutterBottom>
            </Typography>
  <Typography variant="body1">{post.description}</Typography>
  <div className="container">
        <Disqus.DiscussionEmbed
          shortname={disqusShortname}
          config={disqusConfig}
        />
      </div>
  </Container>
  );

}
