import React from 'react';
import PostsService from '../service/PostsService';
import { Link } from 'react-router-dom';
import { Typography, Container, Grid, Box } from "@material-ui/core/";
import { makeStyles } from '@material-ui/core/styles'
import { DeveloperMode } from "@material-ui/icons";
import KeyboardBackspaceIcon from '@material-ui/icons/KeyboardBackspace';
import Button from '@material-ui/core/Button';
import { DiscussionEmbed } from 'disqus-react';

const useStyles = makeStyles(() => ({
  link: {
      textDecoration: 'none'
  }
}))
export default function Blog({ match }) {
  const classes = useStyles()

  const slug = match.params.slug
  const post = PostsService.find(post => post.title == slug.replace('-', ' '))
  console.log(post)

  return (
  <Container component="main" maxWidth="sm">
  <Link to="/blog" className={classes.link}> 
  <Button 

        variant="contained"
        color="default"
        startIcon={<KeyboardBackspaceIcon />}
  >
        Kembali
      </Button>
  </Link>
  <Typography variant="h3" component="h1" gutterBottom>{post.title}</Typography>
            <Typography variant="h5" component="h2" gutterBottom>
            </Typography>
  <Typography variant="body1">{post.description}</Typography>
  <DiscussionEmbed
    shortname='example'
    config={
        {
            url: this.props.article.url,
            identifier: this.props.article.id,
            title: this.props.article.title,
        }
    }
/>
  </Container>
  );

}
