import React from "react";
import CssBaseline from "@material-ui/core/CssBaseline";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import Link from "@material-ui/core/Link";
import FavoriteIcon from "@material-ui/icons/Favorite";
import GitHubIcon from '@material-ui/icons/GitHub';
import FacebookIcon from '@material-ui/icons/Facebook';
import TwitterIcon from '@material-ui/icons/Twitter';
import Grid from '@material-ui/core/Grid';

import Card from './Card';



function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary">
      {"Copyright © "}
      <Link color="inherit" href="https://github.com/bahyusanciko" target="_blank">
        Bahyu Sanciko <GitHubIcon fontSize="small"s /> 
      </Link>{" "}
      {new Date().getFullYear()}
      {"."}
    </Typography>
  );
}


const useStyles = makeStyles(theme => ({
  root: {
    display: "flex",
    flexDirection: "column",
    minHeight: "100vh"
  },
  main: {
    marginTop: theme.spacing(8),
    marginBottom: theme.spacing(2)
  },
  footer: {
    padding: theme.spacing(3, 2),
    marginTop: "auto",
    backgroundColor:
      theme.palette.type === "light"
        ? theme.palette.grey[200]
        : theme.palette.grey[800]
  }
}));

export default function StickyFooter() {
  const classes = useStyles();
  const mainFeaturedPost = {
    title: 'Title of a longer featured blog post',
    description:
      "Multiple lines of text that form the lede, informing new readers quickly and efficiently about what's most interesting in this post's contents.",
    image: 'https://ionicframework.com/docs/v3/img/meta/ionic-framework-og.png',
    imgText: 'main image description',
    linkText: 'Continue reading…',
  };

  const featuredPosts = [
    {
      title: 'Featured post',
      date: 'Nov 12',
      description:
        'This is a wider card with supporting text below as a natural lead-in to additional content.',
      image: 'https://ionicframework.com/docs/v3/img/meta/ionic-framework-og.png',
      imageText: 'Image Text',
    },
    {
      title: 'Post title',
      date: 'Nov 11',
      description:
        'This is a wider card with supporting text below as a natural lead-in to additional content.',
      image: 'https://ionicframework.com/docs/v3/img/meta/ionic-framework-og.png',
      imageText: 'Image Text',
    },
  ];

  return (
    <div className={classes.root}>
      <CssBaseline />
      <Container component="main" className={classes.main} maxWidth="sm">
        <Typography variant="h3" component="h1" gutterBottom>
          Bahyu Sanciko
        </Typography>
        <Typography variant="h5" component="h2" gutterBottom>
          {"Hello Semua,"}
          {"Blog ini hanya berisikan bacotan saya tentang dunia IT sekian. #santuyprogramming"}
        </Typography>
        <Typography variant="h6">Tulisan Terbaru <br/></Typography>
        <Grid container spacing={4}>
            {featuredPosts.map((post) => (
              <Card key={post.title} post={post} />
            ))}
          </Grid>
      </Container>

      <footer className={classes.footer}>
        <Container maxWidth="sm">
          <Typography variant="body1">
            Built with <FavoriteIcon fontSize="small" /> 
          </Typography>
          <Copyright />
        </Container>
      </footer>
    </div>
  );
}
